CREATE TABLE Administration(
   ADM_NAME     VARCHAR(20) 
  ,ADM_POS      VARCHAR(30)
  ,ADM_SALARY   VARCHAR(100)
  ,ADM_WHOURS   VARCHAR(20)
  ,ADM_ID       VARCHAR(7) NOT NULL PRIMARY KEY
  ,ADM_GENDER   CHAR(1) 
  ,ADM_ADDRESS  VARCHAR(100)
  ,ADM_AGE      INTEGER 
  ,ADM_VACATION VARCHAR(10)
  ,ADM_RETIRE   DATE 
  ,ADM_BENEFIT  VARCHAR(100)
);

CREATE TABLE Doctor(
   DOC_SPEC  VARCHAR(100)
  ,DOC_BENEFIT  VARCHAR(200) 
  ,DOC_SALARY   INT  
  ,DOC_AGE      INT  
  ,DOC_NAME     VARCHAR(30) 
  ,DOC_WHOURS   VARCHAR(20) 
  ,DOC_GENDER   CHAR(1) 
  ,DOC_ID       VARCHAR(7)   
  ,DOC_VACATION VARCHAR(10)  
  ,DOC_RETIRE   DATE   
  ,DOC_ADDRESS  VARCHAR(20)  
  ,PRIMARY KEY (DOC_NAME,DOC_ID)
);


CREATE TABLE Useage(
   MED_ID   VARCHAR(7)
  ,PAT_NAME VARCHAR(20)
);

CREATE TABLE Support(
   SUP_NAME       VARCHAR(20)
  ,SUP_ID         VARCHAR(7) PRIMARY KEY
  ,SUP_GENDER     CHAR(1)
  ,SUP_SALARY     INT 
  ,SUP_WHOURS     VARCHAR(20)
  ,SUP_DEPARTMENT VARCHAR(100)
  ,SUP_ADDRESS    VARCHAR(20)
  ,SUP_AGE        INT 
  ,SUP_BENEFIT    VARCHAR(100)
  ,SUP_VACATION   VARCHAR(10)
  ,SUP_RETIRE     DATE 
  ,ADM_ID         VARCHAR(7)
);

CREATE TABLE Nurse(
   NUR_NAME       VARCHAR(20)  
  ,NUR_ID         VARCHAR(7) NOT NULL PRIMARY KEY
  ,NUR_SALARY     INT 
  ,NUR_DEPARTMENT VARCHAR(25)
  ,NUR_WHOURS     VARCHAR(20)
  ,NUR_ADDRESS    VARCHAR(20)
  ,NUR_AGE        INT 
  ,NUR_VACATION   CHAR(10)
  ,NUR_RETIRE     DATE   
  ,NUR_BENEFIT    VARCHAR(100)
);


CREATE TABLE Medicine(
   MED_TYPE    VARCHAR(100)
  ,MED_ID      VARCHAR(7) NOT NULL PRIMARY KEY
  ,MED_PRICE   VARCHAR(6)
  ,MED_AMOUNT  INT 
  ,MED_EXPIRY  VARCHAR(20)
  ,MED_INTAKEM VARCHAR(100)
  ,MED_MDATE   DATE 
  ,MED_NAME    VARCHAR(30)
  ,MED_TRNAME  VARCHAR(30)
  ,MED_ING     VARCHAR(100)
  ,MED_SE      VARCHAR(100)
);


CREATE TABLE Pharmacist(
   PH_NAME     VARCHAR(20)
  ,PH_ID       VARCHAR(7) NOT NULL PRIMARY KEY
  ,PH_SALARY   INT
  ,PH_GENDER   CHAR(1)
  ,PH_WHOURS   VARCHAR(20) 
  ,PH_ADDRESS  VARCHAR(20)
  ,PH_AGE      INT  
  ,PH_VACATION VARCHAR(10)
  ,PH_RETIRE   DATE  
  ,PH_BENEFIT  VARCHAR(100)
);

CREATE TABLE Patients(
   PAT_PREFIX   VARCHAR(10)
  ,PAT_NAME     VARCHAR(20) 
  ,PAT_GENDER   CHAR(1) 
  ,PAT_BLOOD    VARCHAR(2) 
  ,PAT_JOB      VARCHAR(30)
  ,PAT_ADMIT    VARCHAR(5)
  ,PAT_SICKNESS VARCHAR(100)
  ,PAT_RELIGION VARCHAR(20)
  ,PAT_STATUS   VARCHAR(10)
  ,PAT_ADDRESS  VARCHAR(20) 
  ,PAT_PRIV     VARCHAR(10)
  ,PAT_AGE      INT 
  ,PAT_WEIGHT   FLOAT(4)
  ,PAT_BENEFIT  FLOAT(5)
  ,ADM_ID       VARCHAR(7) 
  ,DOC_ID       VARCHAR(7) 
  ,DOC_NAME		VARCHAR(30)
  ,NUR_ID       VARCHAR(7) 
  ,PRIMARY KEY(PAT_NAME)
  ,FOREIGN KEY(ADM_ID) REFERENCES Administration(ADM_ID)
  ,FOREIGN KEY(DOC_NAME,DOC_ID) REFERENCES Doctor(DOC_NAME,DOC_ID)
  ,FOREIGN KEY(NUR_ID) REFERENCES Nurse(NUR_ID)
);

CREATE TABLE Payment_Record(
   PAY_TOTAL    VARCHAR(5)
  ,PAY_ADM      CHAR(10)
  ,PAT_NAME     VARCHAR(20)  
  ,DOC_NAME     VARCHAR(30)  
  ,DOC_ID		VARCHAR(7)  
  ,PAT_PRIV     VARCHAR(10)
  ,PAY_CURE     VARCHAR(100)
  ,PAY_DUR      VARCHAR(5)
  ,PAY_VISITNUM INT 
  ,PAY_APD      DATE 
  ,PAY_ID       VARCHAR(7)  
  ,PRIMARY KEY(PAY_ID)
  ,FOREIGN KEY(DOC_NAME,DOC_ID) REFERENCES Doctor(DOC_NAME,DOC_ID)
  
);

CREATE TABLE Issue(
   PAY_ID VARCHAR(7)
  ,SUP_ID VARCHAR(7)
);


CREATE TABLE Send_Patient(
   DOC_ID VARCHAR(7)
  ,PH_ID  VARCHAR(7)
);

CREATE TABLE Prescript(
   MED_ID VARCHAR(7)
  ,PH_ID  VARCHAR(7)
);

CREATE TABLE Assist(
   DOC_ID VARCHAR(7)
  ,NUR_ID VARCHAR(7)
);

CREATE TABLE Contracts(
   DIS_SCNAME VARCHAR(30)
  ,PAT_NAME   VARCHAR(20)
);

CREATE TABLE Disease(
   DIS_SCNAME   VARCHAR(30) PRIMARY KEY
  ,DIS_SEVERITY VARCHAR(10)
  ,DIS_CARRIER  VARCHAR(100)
  ,DIS_CURE     VARCHAR(100)
  ,DIS_NAME     VARCHAR(30)
  ,DIS_CAUSE    VARCHAR(100)
  ,DIS_FRATE    VARCHAR(4)
  ,DIS_SYMPTOM  VARCHAR(100)
  ,DIS_INFECT   CHAR(1)
);
